import React from "react";
import Footer from "../Footer";
import Navbar from "../Navbar";
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {
  addproductsHandler,
  categoryHndlerData,
  contactHandler,
} from "../../service/auth.service";
import { listBody, validName } from "../../utils/helper";
export default function Contact() {
  const navigate = useNavigate();
  const [cid, setCid] = useState("");
  const [cidErr, setCidErr] = useState("");
  const [firstName, setFirstName] = useState("");
  const [firstNameErr, setFirstNameErr] = useState(false);
  const [lastName, setLastName] = useState("");
  const [lastNameErr, setLastNameErr] = useState(false);
  const [email, setEmail] = useState("");
  const [emailErr, setEmailErr] = useState(false);
  const [phoneNo, setPhoneNo] = useState("");
  const [phoneNoErr, setPhoneNoErr] = useState(false);
  const [msg, setMsg] = useState("");
  const [msgErr, setMsgErr] = useState(false);
  const [selected, setSelected] = useState(false);

  const validation = () => {
    let formIsValid = true;

    return formIsValid;
  };
  const handleSubmit = (e) => {
    if (validation() !== true) {
    } else {
      postData(e);
      setSelected(true);
    }
    e.preventDefault();
  };
  const postData = async (event) => {
    event.preventDefault();
    const body = {
      firstName,
      lastName,
      email,
      phoneNo,
      msg,
    };
    const response = await contactHandler(body);
    if (response.success) {
      setSelected(false);
      setFirstName("");
      setLastName("");
      setEmail("");
      setMsg("");
      setPhoneNo("");
    } else {
      setSelected(false);
    }
  };

  return (
    <>
      <main className="main-wrapper">
        {/* Start Breadcrumb Area  */}
        <div className="axil-breadcrumb-area">
          <div className="container">
            <div className="row align-items-center">
              <div className="col-lg-6 col-md-8">
                <div className="inner">
                  <ul className="axil-breadcrumb">
                    <li className="axil-breadcrumb-item">
                      <a href="index-2.html">Home</a>
                    </li>
                    <li className="separator" />
                    <li
                      className="axil-breadcrumb-item active"
                      aria-current="page"
                    >
                      Contact
                    </li>
                  </ul>
                  <h1 className="title">Contact With Us</h1>
                </div>
              </div>

            </div>
          </div>
        </div>
        {/* End Breadcrumb Area  */}
        {/* Start Contact Area  */}
        <div className="axil-contact-page-area axil-section-gap">
          <div className="container">
            <div className="axil-contact-page">
              <div className="row row--30">
                <div className="col-lg-8">
                  <div className="contact-form">
                    <h3 className="title mb--10">We're Here To Help!</h3>
                    <p>
                      If you’ve got great products your making or looking to
                      work with us then drop us a line.
                    </p>
                    <form
                      id="contact-form"
                      onSubmit={(e) => {
                        handleSubmit(e);
                      }}
                      method="post"
                      className="axil-contact-form"
                    >
                      <div className="row row--10">
                        <div className="col-lg-6">
                          <div className="form-group">
                            <label htmlFor="contact-name">
                              First Name <span>*</span>
                            </label>
                            <input
                              type="text"
                              value={firstName}
                              placeholder="Enter first name"
                              onChange={(e) => [
                                setFirstName(e.target.value),
                                setFirstNameErr(" "),
                              ]}
                            />
                          </div>
                        </div>
                        <div className="col-lg-6">
                          <div className="form-group">
                            <label htmlFor="contact-phone">
                              Last Name <span>*</span>
                            </label>
                            <input
                              type="text"
                              value={lastName}
                              placeholder="Enter last name"
                              onChange={(e) => [
                                setLastName(e.target.value),
                                setLastNameErr(" "),
                              ]}
                            />
                          </div>
                        </div>
                        <div className="col-lg-6">
                          <div className="form-group">
                            <label htmlFor="contact-email">
                              E-mail <span>*</span>
                            </label>
                            <input
                              type="email"
                              value={email}
                              placeholder="Enter email"
                              onChange={(e) => [
                                setEmail(e.target.value),
                                setEmailErr(" "),
                              ]}
                            />
                          </div>
                        </div>
                        <div className="col-lg-6">
                          <div className="form-group">
                            <label htmlFor="contact-email">
                              Phone no <span>*</span>
                            </label>
                            <input
                              type="text"
                              value={phoneNo}
                              placeholder="Enter phone no"
                              onChange={(e) => [
                                setPhoneNo(e.target.value),
                                setPhoneNoErr(" "),
                              ]}
                            />
                          </div>
                        </div>
                        <div className="col-12">
                          <div className="form-group">
                            <label htmlFor="contact-message">
                              Your Message
                            </label>
                            <textarea
                              cols={1}
                              rows={2}
                              value={msg}
                              placeholder="Enter message"
                              onChange={(e) => [
                                setMsg(e.target.value),
                                setMsgErr(" "),
                              ]}
                            />
                          </div>
                        </div>
                        <div className="col-12">
                          <div className="form-group mb--0">
                            <button
                              name="submit"
                              type="submit"
                              id="submit"
