import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { goldProductsListData } from "../../service/auth.service";
import { listBody } from "../../utils/helper";

export default function GoldProductList() {
  const [productData, setproductData] = useState([]);
  const location = useLocation();
  const { search } = location;
  console.log(productData);
  useEffect(() => {
    let userId;
    if (search.split("=").length > 0) {
      userId = search.split("=")[1];
    } else {
      userId = "";
    }
    console.log(userId);
    getcategoryData(userId);
  }, [search]);

  const getcategoryData = async (id) => {
    const response = await goldProductsListData(
      listBody({
        where: {
          isActive: true,
          _id: id,
        },
        perPage: 1000,
      })
    );
    if (response) {
      setproductData(response);
    }
  };
  return (
    <>
      <main className="main-wrapper">
        {/* Start Breadcrumb Area  */}
        <div className="axil-breadcrumb-area">
          <div className="container">
            <div className="row align-items-center">
              <div className="col-lg-6 col-md-8">
                <div className="inner">
                  <ul className="axil-breadcrumb">
                    <li className="axil-breadcrumb-item">
                      <a href="index-2.html">Home</a>
                    </li>
                    <li className="separator" />
                    <li
                      className="axil-breadcrumb-item active"
                      aria-current="page"
                    >
                      Gold Products
                    </li>
                  </ul>
                  <h1 className="title">Explore All Gold Products</h1>
                </div>
              </div>
              <div className="col-lg-6 col-md-4">
                <div className="inner">
                  <div className="bradcrumb-thumb">
                    <img
                      src="assets/images/product/product-45.png"
                      alt="Image"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        {/* End Breadcrumb Area  */}
        {/* Start Shop Area  */}
        <div className="axil-shop-area axil-section-gap bg-color-white">
          <div className="container">
            <div className="row">
              <div className="col-lg-12">
                <div className="axil-shop-top">
                  <div className="row"></div>
                </div>
              </div>
            </div>
            <div className="new-arrivals-product-activation slick-layout-wrapper--15 axil-slick-arrow arrow-top-slide row pt--50">
              {productData?.map((card) => {
                return (
                  <div className="col-3">
                    <div className="axil-product product-style-four">
                      <div className="thumbnail">
                        <a>
                          <img
                            src={card.goldenproductImg}
                            alt="Product Images"
                          />
                        </a>
                      </div>
                      <div className="product-content">
                        <div className="inner">
                          <h5 className="title">
                            <a>{card.goldenproductName}</a>
                          </h5>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}

              {productData.length === 0 ? <>Data not found</> : <></>}
            </div>
            <div className="text-center pt--30">
              <a href="#" className="axil-btn btn-bg-lighter btn-load-more">
                Load more
              </a>
            </div>
          </div>
          {/* End .container */}
        </div>
        {/* End Shop Area  */}
      </main>
    </>
  );
}
