import React, { useEffect, useState } from "react";
import Footer from "../Footer";
import Navbar from "../Navbar";
import {
  bannerHndlerData,
  categoryHndlerData,
} from "../../service/auth.service";
import { listBody } from "../../utils/helper";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay } from "swiper";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";
export default function Home() {
  const [categoriesData, setcategoriesData] = useState([]);
  const [bannerData, setbannerData] = useState([]);
  useEffect(() => {
    getcategoryData();
    getbannerData();
  }, []);

  const getcategoryData = async () => {
    const response = await categoryHndlerData(
      listBody({
        where: {
          isActive: true,
        },
        perPage: 1000,
      })
    );
    if (response) {
      setcategoriesData(response);
    }
  };
  const getbannerData = async () => {
    const response = await bannerHndlerData(
      listBody({
        where: {
          isActive: true,
        },
        perPage: 1000,
      })
    );
    if (response) {
      setbannerData(response);
    }
  };
  return (
    <>
      <Navbar />
      <section className="product-area section-padding">
        <div className="container">
          <div className="row">
            <div className="col-12">
              {/* section title start */}
              <div className="section-title text-center">
                <h2 className="title">our products</h2>
                <p className="sub-title">Add our products to weekly lineup</p>
              </div>
              {/* section title start */}
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <div className="product-container">
                {/* product tab content start */}
                <div className="tab-content">
                  <div className="tab-pane fade active">
                    <div className="product-carousel-4 slick-row-10 slick-arrow-style">
                      {/* product item start */}
                      <div className="product-item">
                        <figure className="product-thumb">
                          <a href="product-details.html">
                            <img
                              className="pri-img"
                              src="https://htmldemo.net/corano/corano/assets/img/product/product-1.jpg"
                              alt="product"
                            />
                            <img
                              className="sec-img"
                              src="https://htmldemo.net/corano/corano/assets/img/product/product-18.jpg"
                              alt="product"
                            />
                          </a>
                          <div className="product-badge">
                            <div className="product-label new">
                              <span>new</span>
                            </div>
                          </div>
                        </figure>
                        <div className="product-caption text-center">
                          <h6 className="product-name">
                            <a href="product-details.html">
                              Perfect Diamond Jewelry
                            </a>
                          </h6>
                        </div>
                      </div>
                      {/* product item end */}
                    </div>
                  </div>
                </div>
                {/* product tab content end */}
              </div>
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </>
  );
}
