import React from "react";
import Footer from "../Footer";
import Navbar from "../Navbar";
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {
  addproductsHandler,
  categoryHndlerData,
} from "../../service/auth.service";
import { listBody, validName } from "../../utils/helper";
import toast from "react-hot-toast";
export default function Contact() {
  const navigate = useNavigate();
  const [cid, setCid] = useState("");
  const [cidErr, setCidErr] = useState("");
  const [productName, setProductsName] = useState("");
  const [productNameErr, setProductsNameErr] = useState(false);
  const [productImg, setProductsImg] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [productDescriptionErr, setProductDescriptionErr] = useState(false);
  const [productImgErr, setProductsImgErr] = useState(false);
  const [selected, setSelected] = useState(false);
  const [categoriesData, setcategoriesData] = useState([]);
  useEffect(() => {
    getcategoryData();
  }, []);
  const getcategoryData = async () => {
    const response = await categoryHndlerData(listBody({ perPage: 1000 }));
    if (response) {
      setcategoriesData(response);
    }
  };

  const validation = () => {
    let formIsValid = true;
    if (!productName) {
      formIsValid = false;
      setProductsNameErr("Your Product name is required");
    }
    if (!productImg) {
      formIsValid = false;
      setProductsImgErr("Your Product image is required");
    }
    if (!productDescription) {
      formIsValid = false;
      setProductDescriptionErr("Your Product description is required");
    }
    if (!validName.test(productName)) {
      formIsValid = false;
      setProductsNameErr("Your Product name is invalid");
    }
    if (!validName.test(productDescription)) {
      formIsValid = false;
      setProductDescriptionErr("Your Product description is invalid");
    }
    if (!cid) {
      formIsValid = false;
      setCidErr("Your Product Category is invalid");
    }

    return formIsValid;
  };
  const handleSubmit = (e) => {
    if (validation() !== true) {
    } else {
      postData(e);
      setSelected(true);
    }
    e.preventDefault();
  };
  const postData = async (event) => {
    event.preventDefault();
    const body = {
      productName,
      productImg,
      productDescription,
      categoryId: cid,
    };
    const response = await addproductsHandler(body);
    if (response.success) {
      setSelected(false);
      navigate("/products");
      successnotify(response.message);
      setSelected(false);
    } else {
      errornotify(response.message);
      setSelected(false);
    }
  };
  const base64 = async (img) => {
    let reader = new FileReader();
    reader.readAsDataURL(img);
    reader.onload = () => {
      setProductsImg(reader.result);
    };
  };

  return (
    <>
      <Navbar />
      <main className="main-wrapper">
        {/* Start Breadcrumb Area  */}
        <div className="axil-breadcrumb-area">
          <div className="container">
            <div className="row align-items-center">
              <div className="col-lg-6 col-md-8">
                <div className="inner">
                  <ul className="axil-breadcrumb">
                    <li className="axil-breadcrumb-item">
                      <a href="index-2.html">Home</a>
                    </li>
                    <li className="separator" />
                    <li
                      className="axil-breadcrumb-item active"
                      aria-current="page"
                    >
                      Contact
                    </li>
                  </ul>
                  <h1 className="title">Contact With Us</h1>
                </div>
              </div>
              <div className="col-lg-6 col-md-4">
                <div className="inner">
                  <div className="bradcrumb-thumb">
                    <img
                      src="assets/images/product/product-45.png"
                      alt="Image"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        {/* End Breadcrumb Area  */}
        {/* Start Contact Area  */}
        <div className="axil-contact-page-area axil-section-gap">
          <div className="container">
            <div className="axil-contact-page">
              <div className="row row--30">
                <div className="col-lg-8">
                  <div className="contact-form">
                    <h3 className="title mb--10">We're Here To Help!</h3>
                    <p>
                      If you’ve got great products your making or looking to
                      work with us then drop us a line.
                    </p>
                    <form
                      id="contact-form"
                      method="POST"
                      action="https://new.axilthemes.com/demo/template/etrade/mail.php"
                      className="axil-contact-form"
                    >
                      <div className="row row--10">
                        <div className="col-lg-4">
                          <div className="form-group">
                            <label htmlFor="contact-name">
                              Name <span>*</span>
                            </label>
                            <input
                              type="text"
                              name="contact-name"
                              id="contact-name"
                            />
                          </div>
                        </div>
                        <div className="col-lg-4">
                          <div className="form-group">
